from flask import Flask,render_template,request,send_fileimport PyPDF2from reportlab.pdfgen import canvasimport randomapp = Flask (__name__)@app.route ('/',  methods=['GET' , 'POST'])def hello_world () :    return render_template("home.html")@app.route('/marker' , methods=['GET' , 'POST'])def marker():    pdf = request.files['pdf']    c = canvas.Canvas('mark.pdf')    c.setFont("Times-Roman" , 72)    c.setFillColorRGB (0, 0, 0, 0.2)    c.drawString(190,370,str(request.form['string']))    c.save()    wat = open("mark.pdf" , "rb")    water = PyPDF2.PdfFileReader(wat)    output = PyPDF2.PdfFileWriter ( )    pdfm = PyPDF2.PdfFileReader (pdf)    for pageNum in range (pdfm.getNumPages()):               page = pdfm.getPage (pageNum)               page.mergePage (water.getPage (0))               output.addPage (page)    with open ('watermarked.pdf', 'wb') as file :             output.write (file)    return render_template('thnaks.html')@app.route('/download')def mypd():    i = int(random.randint(0,10))    p = "watermarked.pdf"    return send_file(p, as_attachment=False, cache_timeout=0.05 )if __name__ == '__main__' :    app.run ()